export class DisplayedObject {
    private value: string | number;
    private style: string;
    
    constructor(value: string | number, style?: string) {
        this.value = value;
        this.style = style;
    }

    setValue(value: string | number) {
        this.value = value;
    }

    getValue(): string | number {
        return this.value;
    }

    getStyle(): string {
        return this.style;
    }
}
