import {DisplayedObject} from "./displayedObject";

export class EditableObject {

    private object: DisplayedObject;
    private propertyName: string;

    constructor(object: DisplayedObject, popertyName: string) {
        this.object = object;
        this.propertyName = popertyName;
    }

    getDisplayedObject(): any {
        return this.object;
    }

    getValue(): string | number {
        return this.object.getValue();
    }

    setValue(value: string | number) {
        this.object.setValue(value);
    }

    getPropertyName(): string {
        return this.propertyName;
    }

    setPropertyName(value: string) {
        this.propertyName = value;
    }
}
