import {TableCell} from "./tableCell";
import {EditableTableCell} from "./editableTableCell";


export class TableRow {

  private tableCells: Array<TableCell|EditableTableCell> = new Array();
  private id: any;

  constructor(id: any, tableCells: Array<TableCell|EditableTableCell>) {
    this.tableCells = tableCells;
    this.id = id;
  }

  getTableCells() {
    return this.tableCells;
  }

  getId() {
    return this.id;
  }
}
